import React, { Component, useState } from "react";
import { withTranslation } from "react-i18next";
import PropTypes from "prop-types";
// import { Link } from "react-router-dom";
import {
  Button,
  Card,
  CardBody,
  CardGroup,
  Col,
  Container,
  Form,
  Input,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Row,
  Label,
} from "reactstrap";
import authentication from "../../../utils/authentication";
import firebase from '../../../firebase';
import 'react-phone-input-2/lib/style.css'; 
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import ForgotPassword from "./ForgotPassword";
import constants from '../../../constants/constant';
import PhoneInput from 'react-phone-input-2'
const authenticationService = new authentication();

class Login extends Component {
  constructor(props) {
    super(props);
    this.handleInputChange = this.handleInputChange.bind(this);
    this.login = this.login.bind(this);
    this.openPopupbox = this.openPopupbox.bind(this);

    this.state = {
      username: "",
      password: "",
      isPopupOpen: false,
      showOTP:false,
      showOTPEntryScreen:false,
      country_code:'',
      count:0,
      changePassword:false
    };
  }
  componentWillMount() {
    if (localStorage.getItem('user_info')) {
      window.location.href = '/dashboard'
    }
    else {
      const messaging = firebase.messaging.isSupported() ? firebase.messaging() : null;
      if(messaging){
      messaging.requestPermission()
        .then(function () {
          return messaging.getToken();
        })
        .then(function (token) {
          localStorage.setItem("fcmToken", token);
        })
        .catch(function (err) {
          console.log("Error " + err);
        })
    }
  }
  }

  async fcmToken(changePassword){
    await authenticationService
      .fcmTokenRequest(localStorage.getItem('fcmToken'))
      .then(response => {
        if (response.status === 200) {
          if(!changePassword){window.location= "/dashboard"}
          else{window.location = "/profile/password"}
        }
        else{
          if (localStorage.getItem('user_info')) {
            if(!changePassword){window.location = "/dashboard"}
          else{window.location = "/profile/password"}
       }
        } 
      });
    }


  login(event) {
    event.preventDefault();
    let changePassword=false;
    authenticationService
      .loginRequest(this.state.username, this.state.password)
      .then(response => {
        if (response.status === 200) {
          localStorage.setItem('entity_id', response.data.entity_id)
          changePassword=response.data.change_password
          this.setUserInfoInLocalStorage(response)
            .then(response => {
              this.fcmToken(changePassword)
            })
          this.setTranslationLanguage()
        }
      });
  }

  setTranslationLanguage() {
    this.props.i18n.changeLanguage(localStorage.getItem("user_preffered_language"));
  }

  genrateOTPlogin (event) {
    var username;
    var country_code;
    if(this.state.showOTP){
    username=this.state.username.slice(-10);
    country_code=this.state.username.replace(username, '')
    } 
    event.preventDefault();
    var params={"mobile_no":username,"country_code":"+"+country_code}
   authenticationService
     .genrateOTP(params)
     .then(response => {
       if (response.status === 200) {
        this.setState({showOTPEntryScreen:true,password:'',username:username,country_code:country_code,userName:username});
       }
     });

 }

  setUserInfoInLocalStorage(apiresponse) {
    return new Promise((resolve, reject) => {
      localStorage.setItem("user_info", JSON.stringify(apiresponse["data"]));
      localStorage.setItem("change_password", JSON.parse(JSON.stringify(apiresponse["data"])).change_password);
      localStorage.setItem("user_preffered_language", JSON.parse(JSON.stringify(apiresponse["data"])).preffered_language)
      localStorage.setItem("isLoggedin", "true");
      localStorage.setItem("token", apiresponse["headers"]["x-auth-token"]);
      localStorage.setItem(
        "serveruniqueid",
        apiresponse["headers"]["server_unique_id"]
      );
      localStorage.setItem(
        "refreshtoken",
        apiresponse["headers"]["refresh-token"]
      );
      resolve();
    })
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  }

  handleInputChangeForUserName(event){
    const target = event.target;
    const value = target.value;
    const name = target.name;
    if(!isNaN(Number(event.target.value)) && event.target.value.length>0){

    this.setState({showOTP:true, [name]: value})
    }
        else {

    this.setState({
    [name]: value,showOTP:false
    });
    }
  }

  handleInputChangeForNumber(value, country, event,formattedValue){
    if(value.length>0){
      this.setState({
        username: value
        });
    }
    if(event.target!=null){
    if(event.target.value=="+"+country.dialCode) {
      this.setState({
        showOTP: false,
        username:""
        });
    } 
  }
}
  openPopupbox() {
    this.setState({ isPopupOpen: !this.state.isPopupOpen });
  }


  render() {
return(
      <div className="app flex-row align-items-center">
        <form className="col-12" onSubmit={this.login}>
          <Container>
            <Row className="justify-content-center">
              <Col md="8">
                <CardGroup>
                  <Card className="p-4">
                    <CardBody>
                      <Form>
                        <h1>Login</h1>
                        <p className="text-muted">Sign in to your account</p>
                        <InputGroup className="mb-3">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="icon-user"></i>
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            type="text"
                            placeholder="Username"
                            autoComplete="username"
                            value={this.state.username}
                            onChange={this.handleInputChangeForUserName.bind(this)}
                            name="username"
                          />
                        </InputGroup>
                        <InputGroup className="mb-4">
                          <InputGroupAddon addonType="prepend">
                            <InputGroupText>
                              <i className="icon-lock"></i>
                            </InputGroupText>
                          </InputGroupAddon>
                          <Input
                            type="password"
                            placeholder="Password"
                            autoComplete="current-password"
                            value={this.state.password}
                            onChange={this.handleInputChange}
                            name="password"
                          />
                        </InputGroup>
                        <Row>
                          <Col xs="6">
                            <Input
                              color="primary"
                              className="px-4 btn btn-primary"
                              disabled={!this.state.username || !this.state.password ? true : false}
                              type="submit"
                              value="Login"
                            />
                            <ToastContainer />
                          </Col>
                        </Row>
                      </Form>
                    </CardBody>
                  </Card>
                </CardGroup>
              </Col>
            </Row>
          </Container>
        </form>
      </div>
      )
                 }
               }

Login.propTypes = {
  classes: PropTypes.object.isRequired
};

export default (withTranslation("translations")(Login));
