import React, { Component } from "react";
import HttpTransferService from "../../../../utils/httptransfer";
import UserAddModal from './addUserModal';
import UserEditModal from './editUserModal';
import UserDeleteModal from './deleteUserModal';
import '../../../../views/modal.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus, faTrash, faPencilAlt } from '@fortawesome/free-solid-svg-icons';
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Alert } from 'reactstrap';
import {  withTranslation } from "react-i18next";
import PropTypes from "prop-types";


const httptransfer = new HttpTransferService();

class Users extends Component {

  constructor(props) {
    super(props);

    this.state = {
      users:[],
      showPopup: false,
      entityId:'',
      userDetails: '',
      showEditUserModal: false,
      showDeleteUserModal: false,
    };
  }

  togglePopup() {
    this.setState({
      showPopup: !this.state.showPopup
    });
    this.getUsers();
  }

  componentDidMount() {
    this.getUsers()
  }

  getUsers() {
    var searchParametre = {'is_deleted':[false]}
    var entity_id = this.props.entityId;
    httptransfer.userQuery(searchParametre, entity_id)
      .then(response => {
        if (response.status === 200) {
          this.setState({
            users : response['data']['users']
           })
        }
      });
  }

  render(){
    const { t, i18n } = this.props;
    let userDetails = ''
    if (this.state.users.length) {
      userDetails = this.state.users.map(item => (
        <tr>
            <td>{item.first_name}</td> 
            <td>{item.last_name}</td>
            <td>{item.mobile}</td>
            <td>{item.email}</td>
        </tr>
      ))
    } else {
      userDetails = <tr className="ml-3"><td colSpan="10"><Alert color="primary">{t("NO_DATA_FOUND")}</Alert></td></tr>
    }
    return (
          <div>
            <ToastContainer />
            <div className="col-sm-12">
            <button onClick={this.togglePopup.bind(this)} className="btn btn-info pull-right mb-3">
            <FontAwesomeIcon icon={faPlus} size="sm" className="mr-1" />Add Airport</button>
              {this.state.showPopup ? 
                  <UserAddModal
                  entityId = {this.props.entityId}
                  showPopup={this.state.showPopup}
                  togglePopup={this.togglePopup.bind(this)}
                  entityType={this.props.entityType}
                />
                : null}
              <table className="table table-responsive-sm table-striped">
                <thead>
                  <tr>
                    <th>Airport Id</th>
                    <th>Airport Name</th>
                    <th>Fuel Capacity</th>
                    <th>Fuel Available</th>
                  </tr>
                </thead>
                <tbody>
                  {userDetails}
                </tbody>
              </table>
            </div>
          </div>  
    );
  }
};

Users.propTypes = {
  classes: PropTypes.object.isRequired
};

export default (Users);
