import React, { Component } from 'react';
import { Label, Input, InputGroupAddon, InputGroup, Button} from 'reactstrap';
import HttpTransferService from "../../../../utils/httptransfer";
import "react-toastify/dist/ReactToastify.css";
import toastService from "../../../../utils/toastnotification";
import { ToastContainer } from "react-toastify";
import { Multiselect } from 'multiselect-react-dropdown';
import constants from '../../../../constants/constant';
import constant from '../../../../constants/constant';
import SimpleReactValidator from 'simple-react-validator';
import validate from "../../../../utils/validations";
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/style.css';
import {  withTranslation } from "react-i18next";
import PropTypes from "prop-types";
import $ from 'jquery';
import Select from 'react-select';

const httptransfer = new HttpTransferService();
const toastservice = new toastService();

class AddTransaction extends Component {

  constructor(props) {
    super(props);
    this.validator = new SimpleReactValidator({autoForceUpdate: this});
    this.handleInputChange = this.handleInputChange.bind(this);

    this.initialState = {
      transactionTypeList:[],
      listOfAirports:[],
      listOfAircrafts:[],
      transactionType:[],
      selectedAirport:[],
      selectedAircraft:[]
    }
    this.state = this.initialState;
  };

   async componentDidMount(){
       this.getTransactionTypeList();
       this.getAirportList();
       this.getAircraftList();
   }

   getTransactionTypeList(){
       let transactionType = [];
         transactionType.push({
             label:"In",
             values:"IN"
         })
         transactionType.push({
             label:"Out",
             values:"OUT"
         })
        this.setState({
              transactionTypeList: transactionType,
         })
   }

   async getAirportList(){
    let airportList = [];  
    let airportListJson = []; 
    await fetch('http://438-ruby-on-rails.code2rock.mindfire-solutions.in/api/v1/airports').
    then(response => response.json())
     .then(data => this.setState({
      airportList:data
     }));

     airportList.map(element=>{
        airportListJson.add({
            label:element.name,
            value:element.id
        })
     })

     this.setState({
        listOfAirports: airportListJson,
     })

   }

   async getAircraftList(){
    let aircraftList = [];  
    let aircraftListJson = []; 
    await fetch('http://438-ruby-on-rails.code2rock.mindfire-solutions.in/api/v1/aircrafts').
    then(response => response.json())
     .then(data => this.setState({
      aircraftList:data
     }));

     aircraftList.map(element=>{
        aircraftListJson.add({
            label:element.airline,
            value:element.id
        })
     })

     this.setState({
        listOfAircrafts: aircraftListJson,
     })

   }

   selectedTransactionType(selectedTransactionType){
      this.setState({
            transactionType: selectedTransactionType,
         })
   }

   selectedAirport(selectedAirport){
      this.setState({
        selectedAirport: selectedAirport,
    })
   }

    selectedAircraft(selectedAircraft){
      this.setState({
        selectedAircraft: selectedAircraft.value,
    })
   }

  handleInputChange(e) {
    let id = e.target.id
    switch (id) {
      case 'quantity':
        this.setState({ quantity: e.target.value });
        break;
      default:
        break;
    }

  }

   async handleSubmit(event) {
 $.ajax({
      url: "http://438-ruby-on-rails.code2rock.mindfire-solutions.in/api/v1/transactions",
      type: "POST",
      data: { aircraft:{aircraft_no:this.state.aircraft_number,
             airline:this.state.airline,
             source:this.state.source,
             destination:this.state.destination

    }},
      success: response => {
        this.props.togglePopup();
        this.props.refresh();
      }
    });

    }

  render() {
    return (
      <div>
        <ToastContainer />
        <div className="my-0 position-relative row form-group">
          <div className="col-6">
            <div className="position-relative form-group">
              <Label for="transaction_type">Transaction type</Label>
                <Select
                    className="basic-single text-capitalize"
                    classNamePrefix="select"
                    onChange={this.selectedTransactionType.bind(this)} 
                    isClearable={true}
                    isSearchable={true}
                    name="transaction_type"
                    id="transaction_type"
                    options={this.state.transactionTypeList}
                    value={this.state.selectedTransactionType?{ label: this.state.selectedTransactionType.label }:null}
                />
            </div>
          </div>
        <div className="col-6">
         <div className="position-relative form-group">
              <Label for="Airport">Airport</Label>
                <Select
                    className="basic-single text-capitalize"
                    classNamePrefix="select"
                    onChange={this.selectedAirport.bind(this)} 
                    isClearable={true}
                    isSearchable={true}
                    name="transaction_type"
                    id="transaction_type"
                    options={this.state.listOfAirports}
                    value={this.state.selectedAirport?{ label: this.state.selectedAirport.label }:null}
                />
            </div>
          </div>
        <div className="col-6">
          <div className="position-relative form-group">
              <Label for="Airport">Aircraft</Label>
                <Select
                    className="basic-single text-capitalize"
                    classNamePrefix="select"
                    onChange={this.selectedAircraft.bind(this)} 
                    isClearable={true}
                    isSearchable={true}
                    name="transaction_type"
                    id="transaction_type"
                    options={this.state.listOfAircrafts}
                    value={this.state.selectedAircraft?{ label: this.state.selectedAircraft.label }:null}
                />
            </div>
          </div>
        <div className="col-6">
            <div className="position-relative form-group">
              <Label for="quantity">Quantity</Label>
              <Input type="number" className="text-capitalize" name="quantity" onChange={this.handleInputChange} id="quantity" placeholder="Enter Quantity" required/>
            </div>
          </div>
        </div>
        <div>
          <button type="submit" className="btn btn-primary btn-md" onClick={(e) => this.handleSubmit(e)}>
            ADD
          </button>
        </div>
      </div>
    )
  }
}

export default AddTransaction;
