import React, { Component } from 'react';
import { Redirect } from 'react-router';
import {  NavLink,Route} from 'react-router-dom';
import { Badge, UncontrolledDropdown, DropdownItem, DropdownMenu, DropdownToggle, Nav, NavItem } from 'reactstrap';
import PropTypes from 'prop-types';
import { AppAsideToggler, AppNavbarBrand, AppSidebarToggler } from '@coreui/react';
import profile from '../../assets/img/profile-avatar.png'
import AuthenticationService from '../../../src/utils/authentication';
import constant from '../.././constants/constant';
import HttpTransferService from "../../../src/utils/httptransfer";
import utils from '../../../src/utils/date'
import firebase from '../../../src/firebase';
import InfiniteScroll from "react-infinite-scroll-component";
import {  withTranslation } from "react-i18next";
import moment from 'moment';

const  authentication = new AuthenticationService()
const httptransfer = new HttpTransferService();


const propTypes = {
  children: PropTypes.node,
};

const defaultProps = {};

class DefaultHeader extends Component {
  constructor(props) {
    super(props);

    this.state = {
        showPopup: false,
        name : '',
        notificationCount:0,
        notificationContent:[],
        notificationIds:[],
        markAllAsRead: false,
        redirectToDashboard: false,
        redirectToScustEnquiry:false,
        redirectToEnquiry: false,
        redirectToTrip: false,
        redirectToQuotation:false,
        redirectToPopUp:false,
        redirectToMessage:false,
        redirectToDriverEnrollRequest:false,
        redirectToEnrolledDriver:false,
        redirectToDocument:false,
        tripId:'',
        pageSize:10,
        hasMoreNotifications:true,
        change_password:false
    }
}


  componentWillMount() {
  }

  componentDidMount() {
console.log("aaaaaaaaaaaaaaa");
  }

togglePopup() {
  this.setState({
    showPopup: !this.state.showPopup
  });
}

  render() {

    // eslint-disable-next-line
    const { children, ...attributes } = this.props;
    const { t, i18n } = this.props;
    return (
      <React.Fragment>
        {this.state.change_password == "false" &&<div>
        <AppSidebarToggler className="d-lg-none" display="md" mobile />
        <p className="navbar-brand font-weight-bold mb-0" style={{color:'#5bcae8'}}>MindFire</p>
        <AppSidebarToggler className="d-md-down-none" display="lg" /></div>}
         {this.state.redirectToMessage?<Redirect to={'/dashboard'}/>:''}
         {this.state.change_password == "false" &&<div>
        <Nav className="d-md-down-none" navbar>
          <NavItem className="px-3">
            <NavLink to="/dashboard" className="nav-link" >{t("DASHBOARD")}</NavLink>
          </NavItem>
        </Nav></div>}
        {/* <AppAsideToggler className="d-md-down-none" /> */}
        {/*<AppAsideToggler className="d-lg-none" mobile />*/}
      </React.Fragment>
    );
  }
}

DefaultHeader.propTypes = propTypes;
DefaultHeader.defaultProps = defaultProps;

DefaultHeader.propTypes = {
  classes: PropTypes.object.isRequired
};

export default (DefaultHeader);
