import React, { Component } from 'react';
import { Label, Input, InputGroupAddon, InputGroup, Button} from 'reactstrap';
import HttpTransferService from "../../../../utils/httptransfer";
import "react-toastify/dist/ReactToastify.css";
import toastService from "../../../../utils/toastnotification";
import { ToastContainer } from "react-toastify";
import { Multiselect } from 'multiselect-react-dropdown';
import constants from '../../../../constants/constant';
import constant from '../../../../constants/constant';
import SimpleReactValidator from 'simple-react-validator';
import validate from "../../../../utils/validations";
import PhoneInput from 'react-phone-input-2';
import 'react-phone-input-2/lib/style.css';
import {  withTranslation } from "react-i18next";
import PropTypes from "prop-types";
import $ from 'jquery';

const httptransfer = new HttpTransferService();
const toastservice = new toastService();

class AddTransaction extends Component {

  constructor(props) {
    super(props);
    this.validator = new SimpleReactValidator({autoForceUpdate: this});
    this.handleInputChange = this.handleInputChange.bind(this);

    this.initialState = {
      transactionTypeList:[]
    }
    this.state = this.initialState;
  };

   componentDidMount(){
       this.getTransactionTypeList();
   }

   getTransactionTypeList(){
         transactionTypeList.push({
             label:"Out",
             values:"OUT"
         })
         transactionTypeList.push({
             label:"Out",
             values:"OUT"
         })
   }

  handleInputChange(e) {
    let id = e.target.id
    switch (id) {
      case 'aircraft_number':
        this.setState({ aircraft_number: e.target.value });
        break;
        case 'airline':
        this.setState({ airline: e.target.value });
        break;
      case 'source':
        this.setState({ source: e.target.value });
        break;
      case 'destination':
        this.setState({ destination: e.target.value });
        break;
      default:
        break;
    }

  }

   async handleSubmit(event) {
 $.ajax({
      url: "http://438-ruby-on-rails.code2rock.mindfire-solutions.in/api/v1/aircrafts",
      type: "POST",
      data: { aircraft:{aircraft_no:this.state.aircraft_number,
             airline:this.state.airline,
             source:this.state.source,
             destination:this.state.destination

    }},
      success: response => {
        this.props.togglePopup();
        this.props.refresh();
      }
    });

    }

  render() {
    return (
      <div>
        <ToastContainer />
        <div className="my-0 position-relative row form-group">
          <div className="col-6">
            <div className="position-relative form-group">
              <Label for="transaction_type">Transaction type</Label>
                <Select
                    className="basic-single text-capitalize"
                    classNamePrefix="select"
                    onChange={this.selectedSourceCustomer.bind(this)} 
                    isClearable={true}
                    isSearchable={true}
                    name="transaction_type"
                    id="transaction_type"
                    options={this.state.sourceCustomerList}
                    value={this.state.selectedSourceCustomer?{ label: this.state.selectedSourceCustomer.label }:null}
                />
            </div>
          </div>
        <div className="col-6">
            <div className="position-relative form-group">
              <Label for="airline">Airline</Label>
              <Input type="text" className="text-capitalize" name="airline" onChange={this.handleInputChange} id="airline" placeholder="Enter airline" required/>
            </div>
          </div>
        <div className="col-6">
            <div className="position-relative form-group">
              <Label for="source">Source</Label>
              <Input type="text" className="text-capitalize" name="source" onChange={this.handleInputChange} id="source" placeholder="Enter source" required/>
            </div>
          </div>
        <div className="col-6">
            <div className="position-relative form-group">
              <Label for="quantity">Quantity</Label>
              <Input type="number" className="text-capitalize" name="quantity" onChange={this.handleInputChange} id="quantity" placeholder="Enter Quantity" required/>
            </div>
          </div>
        </div>
        <div>
          <button type="submit" className="btn btn-primary btn-md" onClick={(e) => this.handleSubmit(e)}>
            ADD
          </button>
        </div>
      </div>
    )
  }
}

export default AddTransaction;
