import React, { Component } from "react";
import '../../../../views/modal.css';
import '../../../../scss/_custom.scss';
import AircraftList from './airCraftList';
import constant from "../../../../constants/constant";
import {  withTranslation } from "react-i18next";
import PropTypes from "prop-types";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

class Reports extends Component {

  constructor(props) {
    super(props);
    this.state = {
    };

  }

    componentDidMount() {

  }

  async getAircrafts() {
    await fetch('http://438-ruby-on-rails.code2rock.mindfire-solutions.in/api/v1/aircrafts').
    then(response => response.json())
  .then(data => {
              //Create a Blob from the PDF Stream
        const file = new Blob([data], { type: "application/pdf" });
        //Build a URL from the file
        const fileURL = URL.createObjectURL(file);
        //Open the URL on new Window
         const pdfWindow = window.open();
         pdfWindow.location.href = fileURL; 
  }
  );
  }

  render() {
    return (
      <div className="mt-4">
        <ToastContainer/>
        <div className="col-sm-12 col-12">
          <div className="card">
            <div className="card-header font-weight-bold">
               Reports
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-xs-6 col-sm-6 col-md-6 col-lg-8 mb-1">
                <a href="#" onClick={()=>this.getAircrafts()}>Airport Summary Report</a>
                </div>
                <div className="col-12">
                <a href="#">Fuel Consumption Report</a>
                </div>

              </div>
            </div>
          </div>
        </div>
        <div>


        </div>

      </div>


    );
  }
};



export default (Reports);
